mapper.select:
select id, arch_no archNo, classfi_id classId, depart_id departId, archives_name archivesName, archives_type archivesType, borrowStatus, create_time createTime, update_time updateTime, create_by createBy, update_by updateBy, checkFlag from archives where delFlag=0
and arch_no =#{archNo}
and archives_name like #{archivesName}
order by id asc

mapper.update:
update archives_classification
set
type = #{type}
where
type_no = #{typeNo}

update archives
set
borrowStatus=#{borrowStatus}
where
arch_no = #{archNo}
